            _jsonOptions.Converters.Add(new PostWildseafisheriesRequestEnterprisesInnerRefrigerantsInnerJsonConverter());
            _jsonOptions.Converters.Add(new PostWildseafisheriesRequestEnterprisesInnerTransportsInnerJsonConverter());
            JsonSerializerOptionsProvider jsonSerializerOptionsProvider = new(_jsonOptions);
